mapper.select:
select number,name,time,teacher,score1,score2
from sc
where username = #{number}

select number,name,time,teacher,score
from course
where number = #{number}
or name = #{name}

delete
from sc
where username=#{username}
and number=#{number}

select number,name,time
from sc
where username = #{username}

select *
from student
where name like '%${name}%';
