mapper.select:
select count(*)
from user
where username = #{username}
  and password = #{password}
  and identity = #{identity}

update user
set password = #{password}
where username = #{username}
    and identity = #{identity}

select identity,username,college,phone
from users
where username = #{username}
  and identity = #{identity}

update users
set phone = #{phone}
where username = #{username}
  and identity = #{identity}

select id
from user
where username = #{username}
  and password = #{password}
  and identity = #{identity}

mapper.update:
update user
set password=#{password}
where id = #{id}

update user
set password=#{password},
    username = #{username}
where id = #{id}
